mapper.select:
select 

from tb_push_info pi

	and pi.info_name like CONCAT('%',#{infoName},'%') 


	and pi.info_type = #{infoType}


	and pi.push_status = #{pushStatus}


    and DATE_FORMAT(pi.create_time, '%Y-%m-%d')  >= #{beginCreateTime}


    and DATE_FORMAT(pi.create_time, '%Y-%m-%d')  <= #{endCreateTime}


    and DATE_FORMAT(pi.create_time, '%Y-%m-%d')  >= #{beginPushTime}


    and DATE_FORMAT(pi.push_time, '%Y-%m-%d')  <= #{endPushTime}

order by pi.push_status desc, pi.create_time desc
