mapper.insert:
insert into t_department (
	name,
	sort
	,parent_id
) values (
	#{name},
	#{sort},
	,#{parent.id}
)

mapper.update:
update t_department set 
name = #{name},
sort = #{sort},
,parent_id = #{parent.id}
where id = #{id}

mapper.select:
select from t_department a LEFT JOIN t_department b ON a.parent_id = b.id
a.name like CONCAT('%',#{name},'%') AND a.parent_id = #{parent.id}
ORDER BY a.id DESC
