update.foreach:
update t_user

	password =#{user.password},
	salt = #{user.salt}

where id = #{user.id}

mapper.select:
select , b.name AS "role.name", c.name AS "department.name" from t_user a LEFT JOIN t_role b ON a.role_id = b.id LEFT JOIN t_department c ON a.department_ = c.id
a.loginName NOT IN
#{name}
AND a.loginName like CONCAT('%',#{loginName},'%') AND a.role_id = #{role.id} AND a.department_id IN
#{dId}
ORDER BY a.id DESC
